pragma solidity ^0.8.4;

/* Contract Imports */
import { L1TokenFactory } from "./L1TokenFactory.sol";
import { ERC20C } from "../tokens/ERC20C.sol";

/**
 * @title L1ERC20C_TokenFactory
 * @dev
 */
contract L1ERC20C_TokenFactory is L1TokenFactory {

    event CreatedERC20C(address contractAddress, string name, string symbol, uint256 initialSupply, address to);

    function create(
        string calldata name,
        string calldata symbol,
        uint256 initialSupply,
        address owner
    ) external override returns (address) {
        require(owner != address(0), "owner address is zero");
        require(initialSupply != 0, "ERC20C is non mintable, so initialSupply must be greater than zero.");

        require(bytes(name).length > 0, "name is empty");
        require(bytes(symbol).length > 0, "symbol is empty");

        ERC20C token = new ERC20C(name, symbol, initialSupply, owner);
        require(address(token) != address(0), "token zero");

        createdContracts[totalCreatedContracts] = ContractInfo(address(token), name, symbol);
        totalCreatedContracts++;

        emit CreatedERC20C(address(token), name, symbol, initialSupply, owner);

        return address(token);
    }

}","keccak256":"0xd87c2c4ee78424abafb5f3165f2574112ecf71197bbf099b437def8f08738c6c","license":"MIT"},"contracts/L1/factory/L1TokenFactory.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "../interfaces/IERC20Factory.sol";

/**
 * @title L1TokenFactory
 * @dev
 */
abstract contract L1TokenFactory is IERC20Factory {

    struct ContractInfo {
        address contractAddress;
        string name;
        string symbol;
    }

    /// @dev Total number of contracts created
    uint256 public totalCreatedContracts ;

    /// @dev Contract information by index
    mapping(uint256 => ContractInfo) public createdContracts;

    /// @dev constructor of ERC20AFactory
    constructor() {
        totalCreatedContracts = 0;
    }

    function create(
        string calldata name,
        string calldata symbol,
        uint256 initialSupply,
        address owner
    ) external override virtual returns (address);

    /// @inheritdoc IERC20Factory
    function lastestCreated() external view override returns (address contractAddress, string memory name, string memory symbol){
        if(totalCreatedContracts > 0){
            return (createdContracts[totalCreatedContracts-1].contractAddress, createdContracts[totalCreatedContracts-1].name, createdContracts[totalCreatedContracts-1].symbol );
        }else {
            return (address(0), '', '');
        }
    }

    /// @inheritdoc IERC20Factory
    function getContracts(uint256 _index) external view override returns (address contractAddress, string memory name, string memory symbol){
        if(_index < totalCreatedContracts){
            return (createdContracts[_index].contractAddress, createdContracts[_index].name, createdContracts[_index].symbol);
        }else {
            return (address(0), '', '');
        }
    }

}","keccak256":"0xe0a60edabf8a9146a6f739e08dc52c79a02c43863c995c5dadf26128220c8fba","license":"MIT"},"contracts/L1/interfaces/IERC20Factory.sol":{"content":"// SPDX-License-Identifier: MIT
