    //     , -chainId- LockIds -  
    // mapping(address => mapping(uint256 => LibLockTOS.LockedBalance)) public lockedBalances;

    //     , -chainId-[ LockIds ]
    mapping(address => mapping(uint256 => uint256[])) public userLocks;

