// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/utils/Arrays.sol";

abstract contract ERC20Snapshot  {
    using Arrays for uint256[];
    using Counters for Counters.Counter;

    // Snapshotted values have arrays of ids and the value corresponding to that id. These could be an array of a
    // Snapshot struct, but that would impede usage of functions that work on an array.
    struct Snapshots {
        uint256[] ids;
        uint256[] values;
    }

    mapping(address => Snapshots) private _accountBalanceSnapshots;
    Snapshots private _totalSupplySnapshots;

    // Snapshot ids increase monotonically, with the first value being 1. An id of 0 is invalid.
    Counters.Counter  private _currentSnapshotId;

    /**
     * @dev Emitted by {_snapshot} when a snapshot identified by `id` is created.
     */
    event Snapshot(uint256 id);

    function currentSnapshotId() public view returns (uint256) {
        return _currentSnapshotId.current();
    }

     /**
     * @dev Creates a new snapshot and returns its snapshot id.
     *
     * Emits a {Snapshot} event that contains the same id.
     *
     * {_snapshot} is `internal` and you have to decide how to expose it externally. Its usage may be restricted to a
     * set of accounts, for example using {AccessControl}, or it may be open to the public.
     *
     * [WARNING]
     * ====
     * While an open way of calling {_snapshot} is required for certain trust minimization mechanisms such as forking,
     * you must consider that it can potentially be used by attackers in two ways.
     *
     * First, it can be used to increase the cost of retrieval of values from snapshots, although it will grow
     * logarithmically thus rendering this attack ineffective in the long term. Second, it can be used to target
     * specific accounts and increase the cost of ERC20 transfers for them, in the ways specified in the Gas Costs
     * section above.
     *
     * We haven't measured the actual numbers; if this is something you're interested in please reach out to us.
     * ====
     */
    function _snapshot() internal virtual returns (uint256) {
        _currentSnapshotId.increment();

        uint256 currentId = _getCurrentSnapshotId();
        emit Snapshot(currentId);
        return currentId;
    }

    /**
     * @dev Get the current snapshotId
     */
    function _getCurrentSnapshotId() internal view virtual returns (uint256) {
        return _currentSnapshotId.current();
    }

    /**
     * @dev Retrieves the balance of `account` at the time `snapshotId` was created.
     */

    function _balanceOfAt(address account, uint256 snapshotId) internal view virtual returns (bool, uint256) {
        (bool snapshotted, uint256 value) = _valueAt(snapshotId, _accountBalanceSnapshots[account]);

        return (snapshotted, value);
        //return snapshotted ? value : balanceOf(account);
    }


    /**
     * @dev Retrieves the total supply at the time `snapshotId` was created.
     */

    function _totalSupplyAt(uint256 snapshotId) internal view virtual returns (bool, uint256) {
        (bool snapshotted, uint256 value) = _valueAt(snapshotId, _totalSupplySnapshots);
        return (snapshotted, value);
        //return snapshotted ? value : totalSupply();
    }

    /*
    // Update balance and/or total supply snapshots before the values are modified. This is implemented
    // in the _beforeTokenTransfer hook, which is executed for _mint, _burn, and _transfer operations.

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        super._beforeTokenTransfer(from, to, amount);

        if (from == address(0)) {
            // mint
            _updateAccountSnapshot(to);
            _updateTotalSupplySnapshot();
        } else if (to == address(0)) {
            // burn
            _updateAccountSnapshot(from);
            _updateTotalSupplySnapshot();
        } else {
            // transfer
            _updateAccountSnapshot(from);
            _updateAccountSnapshot(to);
        }
    }
    */

    function _valueAt(uint256 snapshotId, Snapshots storage snapshots) internal view returns (bool, uint256) {
        require(snapshotId > 0, "ERC20Snapshot: id is 0");
        require(snapshotId <= _getCurrentSnapshotId(), "ERC20Snapshot: nonexistent id");

        // When a valid snapshot is queried, there are three possibilities:
        //  a) The queried value was not modified after the snapshot was taken. Therefore, a snapshot entry was never
        //  created for this id, and all stored snapshot ids are smaller than the requested one. The value that corresponds
        //  to this id is the current one.
        //  b) The queried value was modified after the snapshot was taken. Therefore, there will be an entry with the
        //  requested id, and its value is the one to return.
        //  c) More snapshots were created after the requested one, and the queried value was later modified. There will be
        //  no entry for the requested id: the value that corresponds to it is that of the smallest snapshot id that is
        //  larger than the requested one.
        //
        // In summary, we need to find an element in an array, returning the index of the smallest value that is larger if
        // it is not found, unless said value doesn't exist (e.g. when all values are smaller). Arrays.findUpperBound does
        // exactly this.

        uint256 index = snapshots.ids.findUpperBound(snapshotId);

        if (index == snapshots.ids.length) {
            return (false, 0);
        } else {
            return (true, snapshots.values[index]);
        }
    }
    /*
    function _updateAccountSnapshot(address account) private {
        _updateSnapshot(_accountBalanceSnapshots[account], balanceOf(account));
    }

    function _updateTotalSupplySnapshot() private {
        _updateSnapshot(_totalSupplySnapshots, totalSupply());
    }
    */

    function getAccountBalanceSnapshots(address account) internal view returns (Snapshots storage)  {
        return _accountBalanceSnapshots[account];
    }

    function getTotalSupplySnapshots() internal view returns (Snapshots storage)  {
        return _totalSupplySnapshots;
    }

    function _updateSnapshot(Snapshots storage snapshots, uint256 currentValue) internal {
        uint256 currentId = _getCurrentSnapshotId();
        if (_lastSnapshotId(snapshots.ids) < currentId) {
            snapshots.ids.push(currentId);
            snapshots.values.push(currentValue);
        }
    }

    function _lastSnapshotId(uint256[] storage ids) internal view returns (uint256) {
        if (ids.length == 0) {
            return 0;
        } else {
            return ids[ids.length - 1];
        }
    }

}

contracts/L2/factory/L2TokenFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

/* Contract Imports */
import { AccessibleCommon } from "../../common/AccessibleCommon.sol";

import { L2StandardERC20 } from "../../standards/L2StandardERC20.sol";
import { Lib_PredeployAddresses } from "../../libraries/constants/Lib_PredeployAddresses.sol";


interface IL2Projectmanager {

    function addProject(
        address projectOwner,
        address l1Token,
        address l2Token,
        string calldata projectName
    )  external;
}

/**
 * @title L2TokenFactory
 * @dev Factory contract for creating standard L2 token representations of L1 ERC20s
 * compatible with and working on the standard bridge.
 */
contract L2TokenFactory is AccessibleCommon {

    address public l2ProjectManager;

    event StandardL2TokenCreated(address indexed l1Token, address indexed l2Token);

    modifier nonZeroAddress(address account) {
        require(account != address(0), "Z2");
        _;
    }
    /* ========== DEPENDENCIES ========== */

    /* ========== CONSTRUCTOR ========== */
     constructor() {
        _setRoleAdmin(ADMIN_ROLE, ADMIN_ROLE);
        _setupRole(ADMIN_ROLE, msg.sender);
    }

    /* ========== onlyOwner ========== */
    function setL2ProjectManager(address _l2ProjectManager)
        external nonZeroAddress(_l2ProjectManager) onlyOwner
    {
        require(l2ProjectManager != _l2ProjectManager, "same");
        l2ProjectManager = _l2ProjectManager;
    }

    /* ========== Anyone can execute ========== */

    /**
     * @dev Creates an instance of the standard ERC20 token on L2.
     * @param projectOwner Address of the project owner.
     * @param _l1Token Address of the corresponding L1 token.
     * @param _name ERC20 name.
     * @param _symbol ERC20 symbol.
     */
    function createL2Token(
        address projectOwner,
        address _l1Token,
        string calldata _name,
        string calldata _symbol,
        string calldata projectName
    ) external {
        require(_l1Token != address(0), "Must provide L1 token address");
        require(projectOwner != address(0), "zero projectOwner");
        require(bytes(_name).length != 0, "name is null");
        require(bytes(_symbol).length != 0, "symbol is null");

        L2StandardERC20 l2Token = new L2StandardERC20(
            Lib_PredeployAddresses.L2_STANDARD_BRIDGE,
            _l1Token,
            _name,
            _symbol
        );

        require(address(l2Token) != address(0), "zero l2Token");

        IL2Projectmanager(l2ProjectManager).addProject(
            projectOwner, _l1Token, address(l2Token), projectName
        );

        emit StandardL2TokenCreated(_l1Token, address(l2Token));
    }

    /* ========== VIEW ========== */

    /* === ======= internal ========== */


}

contracts/L2/L2ProjectManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import { AccessibleCommon } from "../common/AccessibleCommon.sol";

import {IERC20} from "../interfaces/IERC20.sol";
import "../libraries/SafeERC20.sol";
import "hardhat/console.sol";

/**
 * @title L2ProjectManager
 * @dev
 */
contract L2ProjectManager is AccessibleCommon {
    using SafeERC20 for IERC20;
    struct ProjectInfo {
        address projectOwner;
        address l1Token;
        address l2Token;
        string projectName;
    }

    bool internal free = true;
    address public l1ProjectManager;
    address public l2TokenFactory;

    // l2token - ProjectInfo
    mapping(address => ProjectInfo) public projects;

    modifier onlyL2TokenFactory() {
        require(l2TokenFactory != address(0) && msg.sender == l2TokenFactory, "caller is not l2TokenFactory");
        _;
    }

    modifier nonZero(uint256 value) {
        require(value != 0, "Z1");
        _;
    }

    modifier nonZeroAddress(address account) {
        require(account != address(0), "Z2");
        _;
    }

    modifier ifFree {
        require(free, "lock");
        free = false;
        _;
        free = true;
    }

    event AddedProject(address indexed l1Token, address indexed l2Token, address projectOwner, string projectName);
    event Hello(string);
    event BalacneOf(address l2Token, uint256 amount);

    /* ========== DEPENDENCIES ========== */

    /* ========== CONSTRUCTOR ========== */
     constructor() {
        _setRoleAdmin(ADMIN_ROLE, ADMIN_ROLE);
        _setupRole(ADMIN_ROLE, msg.sender);
    }

    /* ========== onlyOwner ========== */

    /// @dev l1ProjectManager 주소 설정
    function setL1ProjectManager(address _l1ProjectManager)
        external nonZeroAddress(_l1ProjectManager) onlyOwner
    {
        require(l1ProjectManager != _l1ProjectManager, "same");
        l1ProjectManager = _l1ProjectManager;
    }

    /// @dev l2TokenFactory 주소 설정
    function setL2TokenFactory(address _l2TokenFactory)
        external nonZeroAddress(_l2TokenFactory) onlyOwner
    {
        require(l2TokenFactory != _l2TokenFactory, "same");
        l2TokenFactory = _l2TokenFactory;
    }
    /* ========== only L2TokenFactory ========== */

    /// @dev 프로젝트 추가
    function addProject(
        address projectOwner,
        address l1Token,
        address l2Token,
        string calldata projectName
    )
        external onlyL2TokenFactory
    {
        require(bytes(projectName).length != 0, "projectName is null");
        require(projects[l2Token].l1Token == address(0), "already added");
        projects[l2Token] = ProjectInfo({
            projectOwner: projectOwner,
            l1Token : l1Token,
            l2Token : l2Token,
            projectName : projectName
        });

        emit AddedProject(l1Token, l2Token, projectOwner, projectName);
    }

    /* ========== Anyone can execute ========== */
    function hello(string memory _msg) external {
        emit Hello(_msg);
    }

    function balanceOf(address l2Token) external {
        uint256 balance = IERC20(l2Token).balanceOf(address(this));
        emit BalacneOf(l2Token, balance);
    }
    /* ========== VIEW ========== */

    /* === ======= internal ========== */

}

contracts/L2/vaults/L2CustomVaultA.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import { AccessibleCommon } from "../../common/AccessibleCommon.sol";

import {IERC20} from "../../interfaces/IERC20.sol";
import "../../libraries/SafeERC20.sol";
import "hardhat/console.sol";

/**
 * @title L2CustomVaultA
 * @dev 어드민이 원할때 클래임하는 볼트
 */
contract L2CustomVaultA is AccessibleCommon {
    using SafeERC20 for IERC20;
    bool internal free = true;
    address public l2ProjectManager;

    // l2token - tokenOwner
    mapping(address => address) public vaultAdmin;

    event SetVaultAdmin(address l2Token, address newAdmin);

    modifier onlyL2ProjectManager() {
        require(l2ProjectManager != address(0) && msg.sender == l2ProjectManager, "caller is not l2ProjectManager");
        _;
    }

    modifier nonZero(uint256 value) {
        require(value != 0, "Z1");
        _;
    }

    modifier nonZeroAddress(address account) {
        require(account != address(0), "Z2");
        _;
    }

    modifier ifFree {
        require(free, "lock");
        free = false;
        _;
        free = true;
    }

    /* ========== DEPENDENCIES ========== */

    /* ========== CONSTRUCTOR ========== */
     constructor() {
        _setRoleAdmin(ADMIN_ROLE, ADMIN_ROLE);
        _setupRole(ADMIN_ROLE, msg.sender);
    }

    /* ========== onlyOwner ========== */

    function setL2ProjectManager(address _l2ProjectManager)
        external nonZeroAddress(_l2ProjectManager) onlyOwner
    {
        require(l2ProjectManager != _l2ProjectManager, "same");
        _l2ProjectManager = _l2ProjectManager;
    }

    /* ========== only L2ProjectManager ========== */

    function setVaultAdmin(
        address l2Token,
        address _newAdmin
    )
        external nonZeroAddress(l2Token) nonZeroAddress(_newAdmin) onlyL2ProjectManager
    {

        vaultAdmin[l2Token] = _newAdmin;

        emit SetVaultAdmin(l2Token, _newAdmin);
    }

    /* ========== Anyone can vault admin of token ========== */
    function claim(address l2Token, address to, uint256 amount)
        external nonZeroAddress(msg.sender) nonZeroAddress(l2Token) nonZeroAddress(to) nonZero(amount)
    {
        require(vaultAdmin[l2Token] == msg.sender);
        require(amount <= IERC20(l2Token).balanceOf(address(this)), "balance is insufficient.");

        IERC20(l2Token).safeTransfer(to, amount);
    }

    /* ========== VIEW ========== */

    /* === ======= internal ========== */

}

