//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.4;

// import "../../proxy/Proxy.sol";
import { ProxyStorage } from "../../proxy/ProxyStorage.sol";
import { AccessibleCommon } from "../../common/AccessibleCommon.sol";
import "./L2PublicSaleVaultStorage.sol";

import "../../libraries/SafeERC20.sol";
import '../../libraries/LibProject.sol';

// import "hardhat/console.sol";

interface IVestingFund {
    function publicSaleVault() external view returns(address);
    function isVaultAdmin(address l2Token, address account) external view returns (bool);
    function setVaultAdmin(address l2Token, address newAdmin) external;
    function initialize(
        address l2Token,
        address receivedAddress,
        uint256 claimCounts,
        uint256 firstClaimPercents,
        uint256 firstClaimTime,
        uint256 secondClaimTime,
        uint256 roundInterval,
        uint24 fee
    )
        external;
}

contract L2PublicSaleProxy is
    ProxyStorage,
    AccessibleCommon,
    L2PublicSaleVaultStorage
{
    using SafeERC20 for IERC20;

    /* ========== onlyOwner(proxyContractOwner) ========== */

    function setL2ProjectManager(address _l2ProjectManager)
        external nonZeroAddress(_l2ProjectManager) onlyOwner
    {
        require(l2ProjectManager != _l2ProjectManager, "same");
        l2ProjectManager = _l2ProjectManager;
    }

    function setBurnBridge(
        address _l2Bridge,
        address _l1burnVault
    )
        external
        onlyOwner
    {
        require(l2Bridge != _l2Bridge && l1burnVault != _l1burnVault, "same addr");
        l2Bridge = _l2Bridge;
        l1burnVault = _l1burnVault;
    }

    //_setAddress = quoter, vestingFund, liquidityVault, uniswapRouter, lockTOS, tos, ton
    function initialize(
        address[7] calldata _setAddress,
        uint8 _min,
        uint8 _max,
        uint256 _tier1,
        uint256 _tier2,
        uint256 _tier3,
        uint256 _tier4,
        uint256 _delayTime
    )
        external
        onlyOwner
    {
        setAddress(_setAddress);
        setMaxMinPercent(_min,_max);
        setSTOSstandard(_tier1,_tier2,_tier3,_tier4);
        setDelayTime(_delayTime);
    }

    function setAddress(
        address[7] calldata _setAddress
    )
        public
        onlyOwner
    {
        quoter = _setAddress[0];
        vestingFund = _setAddress[1];
        liquidityVault = _setAddress[2];
        uniswapRouter = _setAddress[3];
        lockTOS = _setAddress[4];
        tos = _setAddress[5];
        ton = _setAddress[6];

        IERC20(ton).approve(
            address(uniswapRouter),
            type(uint256).max
        );

        // IERC20(wton).approve(
        //     address(uniswapRouter),
        //     type(uint256).max
        // );

        // IERC20(ton).approve(
        //     wton,
        //     type(uint256).max
        // );
    }

    function setMaxMinPercent(
        uint8 _min,
        uint8 _max
    )
        public
        onlyOwner
    {
        require(_min < _max, "need min < max");
        minPer = _min;
        maxPer = _max;
    }

    function setSTOSstandard(
        uint256 _tier1,
        uint256 _tier2,
        uint256 _tier3,
        uint256 _tier4
    )
        public
        onlyOwner
    {
        require(
            (_tier1 < _tier2) &&
            (_tier2 < _tier3) &&
            (_tier3 < _tier4),
            "tier set error"
        );
        stanTier1 = _tier1;
        stanTier2 = _tier2;
        stanTier3 = _tier3;
        stanTier4 = _tier4;
    }

    function setDelayTime(
        uint256 _delay
    )
        public
        onlyOwner
    {
        require(delayTime != _delay, "same delayTime");
        delayTime = _delay;
    }

    /* ========== only L2ProjectManager ========== */

    function setVaultAdmin(
        address l2Token,
        address _newAdmin
    )
        external nonZeroAddress(l2Token) nonZeroAddress(_newAdmin) onlyL2ProjectManager
    {
        require(vaultAdminOfToken[l2Token] != _newAdmin, "same");
        vaultAdminOfToken[l2Token] = _newAdmin;
        if(!IVestingFund(vestingFund).isVaultAdmin(l2Token, _newAdmin))  IVestingFund(vestingFund).setVaultAdmin(l2Token,_newAdmin);
        emit SetVaultAdmin(l2Token, _newAdmin);
    }

    /* ========== only VaultAdmin ========== */

    function vaultInitialize(
        address _l2token,
        LibProject.InitalParameterPublicSaleVault memory params,
        LibProject.InitalParameterPublicSaleClaim memory params2,
        LibProject.InitalParameterVestingFundVault memory params3
    )
        external
        onlyVaultAdminOfToken(_l2token)
    {

        setTier(
            _l2token,
            params.stosTier1,
            params.stosTier2,
            params.stosTier3,
            params.stosTier4
        );

        setTierPercents(
            _l2token,
            params.tier1Percents,
            params.tier2Percents,
            params.tier3Percents,
            params.tier4Percents
        );

        setAllAmount(
            _l2token,
            params.total1roundSaleAmount,
            params.total2roundSaleAmount,
            params.saleTokenPrice,
            params.payTokenPrice,
            params.hardcapAmount,
            params.changeTOSPercent,
            0
        );

        set1RoundTime(
            _l2token,
            params.startWhiteTime,
            params.endWhiteTime,
            params.start1roundTime,
            params.end1roundTime
        );

        set2RoundTime(
            _l2token,
            params.snapshotTime,
            params.start2roundTime,
            params.end2roundTime
        );

        setClaimTime(
            _l2token,
            params2.claimCounts,
            params2.firstClaimPercent,
            params2.firstClaimTime,
            params2.secondClaimTime,
            params2.roundInterval
        );

        IVestingFund(vestingFund).initialize(
            _l2token,
            params3.receiveAddress,
            params3.totalClaimCount,
            params3.firstClaimPercent,
            params3.firstClaimTime,
            params3.secondClaimTime,
            params3.roundIntervalTime,
            params3.fee
        );

        IERC20(_l2token).approve(
            address(l2Bridge),
            type(uint256).max
        );

    }

    function setTier(
        address _l2token,
        uint256 _tier1,
        uint256 _tier2,
        uint256 _tier3,
        uint256 _tier4
    )
        public
        onlyVaultAdminOfToken(_l2token)
        nonZero(_tier1)
        nonZero(_tier2)
        nonZero(_tier3)
        nonZero(_tier4)
        beforeStartAddWhiteTime(_l2token)
    {
        if(tiers[_l2token][1] != 0) {
            require(isL2ProjectManager(), "only DAO");
        }
        tiers[_l2token][1] = _tier1;
        tiers[_l2token][2] = _tier2;
        tiers[_l2token][3] = _tier3;
        tiers[_l2token][4] = _tier4;
    }

    function setTierPercents(
        address _l2token,
        uint256 _tier1Percents,
        uint256 _tier2Percents,
        uint256 _tier3Percents,
        uint256 _tier4Percents
    )
        public
        onlyVaultAdminOfToken(_l2token)
        nonZero(_tier1Percents)
        nonZero(_tier2Percents)
        nonZero(_tier3Percents)
        nonZero(_tier4Percents)
        beforeStartAddWhiteTime(_l2token)
    {
        if(tiersPercents[_l2token][1] != 0) {
            require(isL2ProjectManager(), "only DAO");
        }
        require(
            _tier1Percents+(_tier2Percents)+(_tier3Percents)+(_tier4Percents) == 10000,
            "Sum need 10000"
        );
        tiersPercents[_l2token][1] = _tier1Percents;
        tiersPercents[_l2token][2] = _tier2Percents;
        tiersPercents[_l2token][3] = _tier3Percents;
        tiersPercents[_l2token][4] = _tier4Percents;
    }

    function setAllAmount(
        address _l2token,
        uint256 _totalExpectSaleAmount,
        uint256 _totalExpectOpenSaleAmount,
        uint256 _saleTokenPrice,
        uint256 _payTokenPrice,
        uint256 _hardcapAmount,
        uint256 _changePercent,
        uint256 _changeTick
    )
        public
        onlyVaultAdminOfToken(_l2token)
        beforeStartAddWhiteTime(_l2token)
    {
        uint256 balance = IERC20(_l2token).balanceOf(address(this));
        if(balance == 0) {
            IERC20(_l2token).safeTransferFrom(l2ProjectManager, address(this), (_totalExpectSaleAmount+_totalExpectOpenSaleAmount) );
            balance = IERC20(_l2token).balanceOf(address(this));
        }

        require((_totalExpectSaleAmount + _totalExpectOpenSaleAmount) <= balance && 1 ether <= balance, "not input token");
        require(_changePercent <= maxPer && _changePercent >= minPer,"need to set min,max");
        require((_totalExpectSaleAmount+(_totalExpectOpenSaleAmount)) >= (_hardcapAmount*(_payTokenPrice)/(_saleTokenPrice)), "over hardcap");

        LibPublicSaleVault.TokenSaleManage storage manageInfos = manageInfo[_l2token];

        if(manageInfos.set1rdTokenAmount != 0) {
            require(isL2ProjectManager(), "only DAO");
        }
        manageInfos.set1rdTokenAmount = _totalExpectSaleAmount;
        manageInfos.set2rdTokenAmount = _totalExpectOpenSaleAmount;
        manageInfos.saleTokenPrice = _saleTokenPrice;
        manageInfos.tonPrice = _payTokenPrice;
        manageInfos.hardCap = _hardcapAmount;
        manageInfos.changeTOS = _changePercent;
        // manageInfos.changeTick = changeTick;
        if(manageInfos.changeTick == 0) {
            manageInfos.changeTick = 18;
        }
    }

    function set1RoundTime(
        address _l2token,
        uint256 _startAddWhiteTime,
        uint256 _endAddWhiteTime,
        uint256 _startExclusiveTime,
        uint256 _endExclusiveTime
    )
        public
        onlyVaultAdminOfToken(_l2token)
        nonZero(_startAddWhiteTime)
        nonZero(_endAddWhiteTime)
        nonZero(_startExclusiveTime)
        nonZero(_endExclusiveTime)
        beforeStartAddWhiteTime(_l2token)
    {
        LibPublicSaleVault.TokenTimeManage storage timeInfos = timeInfo[_l2token];
        require(
            (_startAddWhiteTime < _endAddWhiteTime) &&
            (_endAddWhiteTime < _startExclusiveTime) &&
            (_startExclusiveTime < _endExclusiveTime),
            "RoundTime err"
        );

        if(timeInfos.deployTime != 0) {
            require(isL2ProjectManager(), "only DAO");
        } else {
            timeInfos.deployTime = block.timestamp;
        }
        // console.log("timeInfos.deployTime :", timeInfos.deployTime);
        // console.log("delayTime :", delayTime);
        // console.log("_startAddWhiteTime :", _startAddWhiteTime);
        require((timeInfos.deployTime + delayTime) < _startAddWhiteTime, "snapshot need later");

        timeInfos.whiteListStartTime = _startAddWhiteTime;
        timeInfos.whiteListEndTime = _endAddWhiteTime;
        timeInfos.round1StartTime = _startExclusiveTime;
        timeInfos.round1EndTime = _endExclusiveTime;
    }

    function set2RoundTime(
        address _l2token,
        uint256 _snapshot,
        uint256 _startDepositTime,
        uint256 _endDepositTime
    )
        public
        onlyVaultAdminOfToken(_l2token)
        nonZero(_snapshot)
        nonZero(_startDepositTime)
        nonZero(_endDepositTime)
        beforeStartAddWhiteTime(_l2token)
    {
        LibPublicSaleVault.TokenTimeManage storage timeInfos = timeInfo[_l2token];
         if(timeInfos.snapshot != 0) {
            require(isL2ProjectManager(), "only DAO");
        }

        require(
            (_startDepositTime < _endDepositTime),
            "Round2time err"
        );

        timeInfos.snapshot = _snapshot;
        timeInfos.round2StartTime = _startDepositTime;
        timeInfos.round2EndTime = _endDepositTime;
    }

    function setClaimTime(
        address _l2token,
        uint256 _claimCounts,
        uint256 _firstClaimPercents,
        uint256 _firstClaimTime,
        uint256 _secondClaimTime,
        uint256 _roundInterval
    )
        public
        onlyVaultAdminOfToken(_l2token)
        nonZero(_claimCounts)
        beforeStartAddWhiteTime(_l2token)
    {
        LibPublicSaleVault.TokenSaleClaim storage claimInfos = claimInfo[_l2token];
        require(_firstClaimTime > block.number, "first claim time passed");
        require(_firstClaimTime != 0, "firstclaimTime value err");
        if(claimInfos.totalClaimCounts != 0) {
            require(isL2ProjectManager(), "only DAO");
        }

        claimInfos.totalClaimCounts = _claimCounts;
        claimInfos.firstClaimPercent = _firstClaimPercents;
        claimInfos.firstClaimTime = _firstClaimTime;
        claimInfos.secondClaimTime = _secondClaimTime;
        claimInfos.claimInterval = _roundInterval;
    }

    /* ========== VIEW ========== */

    function isL2ProjectManager() public view returns (bool) {
        return (l2ProjectManager != address(0) && msg.sender == l2ProjectManager);
    }

    function isVaultAdmin(address l2Token, address account) public view returns (bool) {
        return (account != address(0) && vaultAdminOfToken[l2Token] == account);
    }

    //address(0)이면 해당 l2Token에 대해서 admin이 설정되지 않앗음 -> projectManager로 부터 판매허용이 안떨어졌음
    //address(0)가 아니면 l2Token에 대해서 admin이 설정되었으므로 판매허용이 떨어졌음
    function isL2Token(address l2Token) public view returns (bool) {
        return (vaultAdminOfToken[l2Token] != address(0));
    }
}

contracts/L2/vaults/L2PublicSaleVaultSto.content:
//SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import { LibPublicSaleVault } from "../../libraries/LibPublicSaleVault.sol";

contract L2PublicSaleVaultStorage {
    uint24 public constant poolFee = 3000;

    address public constant wton = 0xc4A11aaf6ea915Ed7Ac194161d2fC9384F15bff2;

    address public quoter;
    address public vestingFund;
    address public liquidityVault;
    address public uniswapRouter;

    address public lockTOS;
    address public tos;
    address public ton;

    //L2PublicSaleContract을 manage하는 manager
    address public l2ProjectManager;
    address public l2Bridge;
    address public l1burnVault;

    //관리자가 설정하는 min,maxPer (changeTOS를 결정할 수 있는 범위)
    uint8 public minPer;    //현재는 소수점 자리수가 없이 사용되어서 uint8(0~255까지 범위)이 사용됨.
    uint8 public maxPer;

    //관리자가 설정하는 stanTier
    uint256 public stanTier1;     //최소 기준 Tier1 기준이 제일 작음
    uint256 public stanTier2;     //최소 기준 Tier2
    uint256 public stanTier3;     //최소 기준 Tier3
    uint256 public stanTier4;     //최소 기준 Tier4 기준이 제일 큼

    //관리자가 설정하는 contract를 deploy 후 snapshot을 지정할 수 있는 최소 시간 간격
    uint256 public delayTime;

    //관리자가 설정하는 changeTick
    int24 public changeTick;    //TON -> TOS로 변경할때 허용되는 Tick 범위

    bool internal free = true;

    //l2token의 모든 user의 totalDepositAmount
    mapping(address => uint256) public totalDepositAmount;

    //l2token이 기준이 된다. (l2token - tokenOwner)
    mapping(address => address) public vaultAdminOfToken;


    mapping(address => address[]) public depositors;            // l2token - depositors
    mapping(address => address[]) public whitelists;            // l2token - whitelists

    mapping(address => uint256[]) public claimTimes;            // l2token - claimTimes
    mapping(address => uint256[]) public claimPercents;         // l2token - claimPercents


    // l2token - struct
    mapping(address => LibPublicSaleVault.TokenTimeManage) public timeInfo;     // l2token - timeInfo
    mapping(address => LibPublicSaleVault.TokenSaleManage) public manageInfo;       // l2token - manageInfo
    mapping(address => LibPublicSaleVault.TokenSaleClaim) public claimInfo;       // l2token - manageInfo
    mapping(address => LibPublicSaleVault.TokenSaleInfo) public saleInfo;       // l2token - saleInfo

    // l2token - account - struct
    mapping(address => mapping(address => LibPublicSaleVault.UserInfo1rd)) public user1rd;      //l2token - userAccount - userInfo1rd
    mapping(address => mapping(address => LibPublicSaleVault.UserInfo2rd)) public user2rd;      //l2token - userAccount - userInfo2rd
    mapping(address => mapping(address => LibPublicSaleVault.UserClaim)) public userClaim;      //l2token - userAccount - userClaim    


    // l2token - tierInfo
    mapping(address => mapping(uint8 => uint256)) public tiers;                 // l2token - tierNumber - tier sTOS value
    mapping(address => mapping(uint8 => uint256)) public tiersPercents;         // l2token - tierNumber - tier SalePercent (tierPercents sum is 10000)
    mapping(address => mapping(uint8 => uint256)) public tiersWhiteList;        // l2token - tierNumber - Number of whitelist participants by tier
    mapping(address => mapping(uint8 => uint256)) public tiersCalculAccount;    // l2token - tierNumber - Number of calculator for Amount by tier
    mapping(address => mapping(uint8 => uint256)) public tiers1stAccount;       // l2token - tierNumber - Number of 1round participants by tier

    event SetVaultAdmin(address l2Token, address newAdmin);
    event SetInitializer(address newInitializer);

    event AddedWhiteList(address l2Token, address indexed from, uint256 tier);
    event ExclusiveSaled(address l2Token, address indexed from, uint256 amount);
    event Deposited(address l2Token, address indexed from, uint256 amount);

    event Claimed(address l2Token, address indexed from, uint256 amount);
    event Refunded(address l2Token, address indexed from, uint256 amount);

    event DepositWithdrawal(address l2Token, address indexed from, uint256 amount, uint256 liquidityAmount);
    event ExchangeSwap(address l2Token, address indexed from, uint256 amountIn, uint256 amountOut);

    modifier onlyL2ProjectManager() {
        require(l2ProjectManager != address(0) && msg.sender == l2ProjectManager, "caller is not l2ProjectManager");
        _;
    }

    modifier onlyVaultAdminOfToken(address l2token) {
        require(vaultAdminOfToken[l2token] != address(0) && msg.sender == vaultAdminOfToken[l2token] || msg.sender == l2ProjectManager, "caller is not a vaultAdmin Of l2Token");
        _;
    }

    modifier nonZero(uint256 value) {
        require(value != 0, "Z1");
        _;
    }

    modifier nonZeroAddress(address account) {
        require(account != address(0), "Z2");
        _;
    }

    modifier beforeStartAddWhiteTime(address l2token) {
        LibPublicSaleVault.TokenTimeManage memory timeInfos = timeInfo[l2token];
        require(
            timeInfos.whiteListStartTime == 0 ||
                (timeInfos.whiteListStartTime > 0 && block.timestamp < timeInfos.whiteListStartTime),
            "not beforewhiteListStartTime"
        );
        _;
    }

    modifier ifFree {
        require(free, "lock");
        free = false;
        _;
        free = true;
    }
}

contracts/L2/vaults/L2VestingFundVault.s.content:
//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.4;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import { ProxyStorage } from "../../proxy/ProxyStorage.sol";
import { AccessibleCommon } from "../../common/AccessibleCommon.sol";
import { L2VestingFundVaultStorage } from "./L2VestingFundVaultStorage.sol";

import { LibVestingFundVault } from "../../libraries/LibVestingFundVault.sol";

// import "hardhat/console.sol";

interface IIUniswapV3Pool {
    function slot0()
        external
        view
        returns (
            uint160 sqrtPriceX96,
            int24 tick,
            uint16 observationIndex,
            uint16 observationCardinality,
            uint16 observationCardinalityNext,
            uint8 feeProtocol,
            bool unlocked
        );
}

contract L2VestingFundVault is
    ProxyStorage,
    AccessibleCommon,
    L2VestingFundVaultStorage
{
    // using SafeERC20 for IERC20;


    ///@dev constructor
    constructor() {

    }

    function changeReceivedAddress(
        address _l2Token,
        address _receivedAddress
    )
        external
        nonZeroAddress(_receivedAddress)
    {
        require(msg.sender == receivedAddress[_l2Token] || isVaultAdmin(_l2Token,msg.sender), "caller is not receivedAddress or vaultAdmin");
        receivedAddress[_l2Token] = _receivedAddress;
    }


    function ownerSetting(
        address _l2Token,
        uint256 _claimCounts,
        uint256 _firstClaimPercents,
        uint256 _firstClaimTime,
        uint256 _secondClaimTime,
        uint256 _roundInterval
    )
        external
        onlyOwner
    {
        _initialize(
            _l2Token,
            _claimCounts,
            _firstClaimPercents,
            _firstClaimTime,
            _secondClaimTime,
            _roundInterval
        );

        if(settingChecks[_l2Token] != true) settingChecks[_l2Token] = true;
    }

    function initialize(
        address _l2Token,
        address _receivedAddress,
        uint256 _claimCounts,
        uint256 _firstClaimPercents,
        uint256 _firstClaimTime,
        uint256 _secondClaimTime,
        uint256 _roundInterval,
        uint24 _fee
    )
        external
        nonZeroAddress(_l2Token)
        nonZeroAddress(_receivedAddress)
        // onlyVaultAdminOfToken(_l2Token)
        onlyL2PublicSale
    {

        require(settingChecks[_l2Token] != true, "Already initalized");
        receivedAddress[_l2Token] = _receivedAddress;
        fees[_l2Token] = _fee;

        _initialize(
            _l2Token,
            _claimCounts,
            _firstClaimPercents,
            _firstClaimTime,
            _secondClaimTime,
            _roundInterval
        );

        settingChecks[_l2Token] = true;
    }

    function _initialize(
        address _l2Token,
        uint256 _claimCounts,
        uint256 _firstClaimPercents,
        uint256 _firstClaimTime,
        uint256 _secondClaimTime,
        uint256 _roundInterval
    )
        internal
    {

        require(_claimCounts != 0,
                "claimCounts must be greater than zero");

        LibVestingFundVault.VaultInfo storage info = vaultInfo[_l2Token];
        info.totalClaimCount = _claimCounts;
        info.firstClaimPercents = _firstClaimPercents;
        info.firstClaimTime = _firstClaimTime;
        info.secondClaimTime = _secondClaimTime;
        info.roundInterval = _roundInterval;

        emit InitializedL2VestingFundVault(_l2Token, _claimCounts, _firstClaimPercents, _firstClaimTime, _secondClaimTime, _roundInterval);
    }

    function claim(
        address _l2Token
    )
        public
    {
        // require(currentSqrtPriceX96(_l2Token) != 0, "pool's current sqrtPriceX96 is zero.");
        LibVestingFundVault.VaultInfo memory info = vaultInfo[_l2Token];
        require(info.firstClaimTime != 0 && block.timestamp > info.firstClaimTime, "Vault: not started yet");
        require(totalAllocatedAmount[_l2Token] > totalClaimsAmount[_l2Token],"Vault: already All get");
        _claim(_l2Token);
    }

    function _claim(
        address _l2Token
    )
        internal
    {
        uint256 curRound = currentRound(_l2Token);
        uint256 amount = calculClaimAmount(_l2Token);
        require(amount > 0, "claimable amount is zero");
        require(IERC20(tonToken).balanceOf(address(this)) >= amount,"Vault: insufficient balance");
        require(remainAmount(_l2Token) >= amount,"Vault: over remain balance");

        nowClaimRound[_l2Token] = curRound;
        totalClaimsAmount[_l2Token] = totalClaimsAmount[_l2Token] + amount;

        IERC20(tonToken).transfer(receivedAddress[_l2Token], amount);

        emit Claimed(msg.sender, receivedAddress[_l2Token], amount);
    }

    function funding(
        address _l2Token,
        uint256 amount
    )
        external
        onlyL2PublicSale
    {
        // require(currentSqrtPriceX96(_l2Token) != 0, "pool's current sqrtPriceX96 is zero.");
        LibVestingFundVault.VaultInfo memory info = vaultInfo[_l2Token];
        require(info.totalClaimCount != 0, "set up a claim round for vesting");

        require(msg.sender == publicSaleVault, "caller is not publicSaleVault.");
        require(IERC20(tonToken).allowance(publicSaleVault, address(this)) >= amount, "funding: insufficient allowance");
        totalAllocatedAmount[_l2Token] += amount;
        IERC20(tonToken).transferFrom(publicSaleVault, address(this), amount);

        emit Funded(msg.sender, amount);

        uint256 curRound = currentRound(_l2Token);

        if (curRound > 0 && calculClaimAmount(_l2Token) > 0 && totalAllocatedAmount[_l2Token] > totalClaimsAmount[_l2Token]) {
            _claim(_l2Token);
        }
    }

    /* === ======= internal ========== */


    /* ========== VIEW ========== */

    function currentRound(
        address _l2Token
    )
        public
        view
        returns (uint256 round)
    {
        LibVestingFundVault.VaultInfo memory info = vaultInfo[_l2Token];
        if(info.firstClaimTime == 0) return 0;
        if(info.firstClaimTime > block.timestamp) return 0;
        if(info.firstClaimTime != 0 && info.firstClaimTime <= block.timestamp && block.timestamp < info.secondClaimTime) {
            round = 1;
        } else if(info.secondClaimTime <= block.timestamp) {
            round = (block.timestamp - info.secondClaimTime) / info.roundInterval + 2;
        }
        if (round > info.totalClaimCount) round = info.totalClaimCount;
    }

    function calculClaimAmount(
        address _l2Token
    )
        public
        view
        returns (uint256 amount)
    {
        uint256 curRound = currentRound(_l2Token);
        if (curRound == 0) return 0;

        LibVestingFundVault.VaultInfo memory info = vaultInfo[_l2Token];
        if(nowClaimRound[_l2Token] < curRound) {
            if (curRound == 1) {
                amount = totalAllocatedAmount[_l2Token]*info.firstClaimPercents/(10000) - totalClaimsAmount[_l2Token];
            } else if (curRound < info.totalClaimCount) {
                amount = totalAllocatedAmount[_l2Token]*info.firstClaimPercents/(10000);
                amount = (amount + ((totalAllocatedAmount[_l2Token] - amount)/(info.totalClaimCount-1) * (curRound -1))) - totalClaimsAmount[_l2Token];
            } else {
                amount = totalAllocatedAmount[_l2Token] - totalClaimsAmount[_l2Token];
            }
        }
    }

    function remainAmount(
        address _l2Token
    )
        public
        view
        returns (uint256 amount)
    {
        return totalAllocatedAmount[_l2Token] - totalClaimsAmount[_l2Token];
    }

    function getPoolAddress(
        address _l2Token
    )
        public
        view
        returns (address pool)
    {
        //L2에서는 변경되어야함
        bytes32 POOL_INIT_CODE_HASH = 0xe34f199b19b2b4f47f68442619d555527d244f78a3297ea89325f843f87b8b54;

        if (tosToken == address(0) || _l2Token == address(0) || fees[_l2Token] == 0
            || uniswapV3Factory == address(0) )  return address(0);

        address token0 = tosToken;
        address token1 = _l2Token;
        if (tosToken > _l2Token) {
            token0 = _l2Token;
            token1 = tosToken;
        }

        pool = address(
            uint160(
            uint256(
                keccak256(
                    abi.encodePacked(
                        hex'ff',
                        uniswapV3Factory,
                        keccak256(abi.encode(token0, token1, fees[_l2Token])),
                        POOL_INIT_CODE_HASH
                    )
                )
            ))
        );
    }

    function currentSqrtPriceX96(
        address _l2Token
    )
        public
        view
        returns (uint160 sqrtPriceX96)
    {
        sqrtPriceX96 = 0;
        address pool = getPoolAddress(_l2Token);
        // console.log("pool", pool);
        if (pool != address(0) && isContract(pool)) {
            // (,tick,,,,,) = IIUniswapV3Pool(pool).slot0();
            (sqrtPriceX96,,,,,,) = IIUniswapV3Pool(pool).slot0();
        }
    }

    function isContract(address _addr) public view returns (bool _isContract) {
        uint32 size;
        assembly {
            size := extcodesize(_addr)
        }
        return (size > 0);
    }

    // function availableInitializer(
    //     address _l2Token,
    //     address _addr
    // )
    //     external
    //     view
    //     returns (bool result)
    // {
    //     if (!settingCheck && (_addr == receivedAddress || isAdmin(_addr))) result = true;
    // }

    // function isL2PublicSale(address account) public view returns (bool) {
    //     return (account != address(0) && publicSaleVault == account);
    // }

    function isVaultAdmin(address l2Token, address account) public view returns (bool) {
        return (account != address(0) && vaultAdminOfToken[l2Token] == account);
    }

}

